var a=class{constructor(r){this.randomFn=r||Math.random;}integer(r=0,n=100){if(r>n)throw new Error("Min value cannot be greater than max value.");return Math.floor(this.randomFn()*(n-r+1))+r}float(r=0,n=100,e=2){if(e<0)throw new Error("Precision cannot be negative.");let t=Math.pow(10,e);return Math.round((this.randomFn()*(n-r)+r)*t)/t}intArray(r,n=0,e=100){if(r<0)throw new Error("Length cannot be negative.");if(n>e)throw new Error("Min value cannot be greater than max value.");return Array.from({length:r},()=>this.integer(n,e))}floatArray(r,n=0,e=100,t=2){if(r<0)throw new Error("Length cannot be negative.");if(t<0)throw new Error("Precision cannot be negative.");if(n>e)throw new Error("Min value cannot be greater than max value.");return Array.from({length:r},()=>this.float(n,e,t))}boolean(){return this.randomFn()<.5}pick(r){return r.length===0?null:r[this.integer(0,r.length-1)]}sample(r,n){if(n>r.length)throw new Error("Count cannot be greater than array length.");let e=[],t=new Set;for(;e.length<n;){let o=this.integer(0,r.length-1);t.has(o)||(e.push(r[o]),t.add(o));}return e}shuffle(r){let n=[...r];for(let e=n.length-1;e>0;e--){let t=this.integer(0,e);[n[e],n[t]]=[n[t],n[e]];}return n}string(r,n="ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789"){if(r<0)throw new Error("Length cannot be negative.");return Array.from({length:r},()=>this.sample(n.split(""),1)[0]).join("")}};

